// For test access
window.events = [];

var onEvent = function(topic, name, payload) {
  var eventString = topic + '.' + name;
  if (typeof payload !== 'undefined') {
    eventString += (': ' + payload);
  }
  console.log(eventString);
  // For test access
  window.events.push(eventString);
};

module.exports = onEvent;
